    private void btnClearEActionPerformed(java.awt.event.ActionEvent evt) {
        // reverts to last entered value (can't be used with numbers on screen)
        if (Screen.getText().isEmpty()) {
            Screen.setText("" + userValue);
        }
    }

    private void btnBkSpaceActionPerformed(java.awt.event.ActionEvent evt) {
        char ch = Screen.getText().charAt(Screen.getText().length() - 1);
        boolean trues = Character.isDigit(ch);
        if (trues) {
            Screen.setText(Screen.getText().substring(0,
                    Screen.getText().length() - 1));
        }
        // Screen.setText(Screen.getText().substring(0,
        // Screen.getText().length()-1));
    }

