    private void btnEqualActionPerformed(java.awt.event.ActionEvent evt) {
        value = Double.parseDouble(Screen.getText());
        // System.out.println(SolveTEMP);
        if (addBool == true) {
            value = value + userValue;
        } else if (subBool == true) {
            if (userValue >= value) {
                value = userValue - value;
            } else
                value = userValue - value;
        } else if (mulBool == true) {
            value = value * userValue;
        } else if (divBool == true) {
            value = userValue / value;
        }
        Screen.setText(Double.toString(value));

        addBool = false;
        subBool = false;
        mulBool = false;
        divBool = false;
        // btnClearActionPerformed(evt);
    }

    private void btnRootActionPerformed(java.awt.event.ActionEvent evt) {
        double value = Math.sqrt(Double.parseDouble(Screen.getText()));
        Screen.setText(Double.toString(value));
    }

